    function _calculateTokenBalance(
        address token,
        address user
    ) internal view override returns (uint256) {
        // Validate token and get necessary details
        (
            address validToken,
            address originalToken,
            bool isExternalToken
        ) = _validateTokenAndGetDetails(token);

