pragma solidity 0.8.20;

import {DLoopWithdrawerBase, ERC20, IERC3156FlashLender, DLoopCoreBase} from "../../DLoopWithdrawerBase.sol";
import {CurveSwapLogic, CurveHelper, ICurveRouterNgPoolsOnlyV1} from "./CurveSwapLogic.sol";

/**
 * @title DLoopWithdrawerCurve
 * @dev Implementation of DLoopWithdrawerBase with Curve swap functionality
 */
contract DLoopWithdrawerCurve is DLoopWithdrawerBase {
    ICurveRouterNgPoolsOnlyV1 public immutable curveRouter;
    uint256 public immutable maxSlippageSurplusSwapBps;

    mapping(string => CurveHelper.CurveSwapExtraParams)
        public defaultSwapParams;
    mapping(string => bool) public isSwapParamsSet;

    struct CurveSwapExtraParamsDefaultConfig {
        address inputToken;
        address outputToken;
        CurveHelper.CurveSwapExtraParams swapExtraParams;
        CurveHelper.CurveSwapExtraParams reverseSwapExtraParams;
    }

    /**
     * @dev Constructor for the DLoopWithdrawerCurve contract
     * @param _underlyingAsset Address of the underlying asset
     * @param _dStable Address of the dStable token
     * @param _flashLender Address of the flash loan provider
     * @param _dLoopCore Address of the dLoopCore contract
     * @param _curveRouter Address of the Curve router
     * @param _swapSlippageTolerance Swap slippage tolerance in basis points
     * @param _maxSlippageSurplusSwapBps Maximum slippage for surplus swap in basis points
     * @param _defaultSwapParamsList List of default swap parameters
     */
    constructor(
        ERC20 _underlyingAsset,
        ERC20 _dStable,
        IERC3156FlashLender _flashLender,
        DLoopCoreBase _dLoopCore,
        ICurveRouterNgPoolsOnlyV1 _curveRouter,
        uint256 _swapSlippageTolerance,
        uint256 _maxSlippageSurplusSwapBps,
        CurveSwapLogic.CurveSwapExtraParamsDefaultConfig[]
            memory _defaultSwapParamsList
    )
        DLoopWithdrawerBase(
            _underlyingAsset,
            _dStable,
            _flashLender,
            _dLoopCore,
            _swapSlippageTolerance
        )
    {
        curveRouter = ICurveRouterNgPoolsOnlyV1(_curveRouter);
        maxSlippageSurplusSwapBps = _maxSlippageSurplusSwapBps;

        // Initialize default swap parameters
        CurveSwapLogic.initializeDefaultSwapParams(
            _defaultSwapParamsList,
            defaultSwapParams,
            isSwapParamsSet
        );
    }

    /**
     * @inheritdoc DLoopWithdrawerBase
     * @dev Swaps an exact amount of output tokens for the minimum input tokens
     */
    function _swapExactOutput(
        ERC20 inputToken,
        ERC20 outputToken,
        uint256 amountOut,
        uint256 amountInMaximum,
        address receiver,
        uint256, // deadline (not used in Curve)
        bytes memory extraData
    ) internal override returns (uint256) {
        return
            CurveSwapLogic.swapExactOutput(
                inputToken,
                outputToken,
                amountOut,
                amountInMaximum,
                receiver,
                extraData,
                curveRouter,
                defaultSwapParams,
                isSwapParamsSet,
                maxSlippageSurplusSwapBps
            );
    }

    /**
     * @dev Sets the swap extra params for a given input and output token
     * @param _swapExtraParamsConfig The swap extra params config
     */
    function setSwapExtraParams(
        CurveSwapLogic.CurveSwapExtraParamsDefaultConfig
            memory _swapExtraParamsConfig
    ) external onlyOwner {
        CurveSwapLogic.setSwapExtraParams(
            _swapExtraParamsConfig,
            defaultSwapParams,
            isSwapParamsSet
        );
    }
}
